Alias.script:
echo("Welcome to Mudlet!\n")
echo("Let's adjust a few preferences to improve the visually impaired experience:\n")

setConfig("autoClearInputLine", true)
echo("Text will now be removed from the input line after it was sent ✓\n")
setConfig("showSentText", false)
echo("Text sent to the game will not appear in the main window ✓\n")

setConfig("caretShortcut", "ctrltab")
echo("Shortcut to switch between input line and main window set to Ctrl+Tab. You can also change it to either Tab or F6 in settings.\n")

if getOS() == "windows" then
  setConfig("blankLinesBehaviour", "hide")
  echo("Blank lines will be removed from the output ✓\n")
end

if getOS() == "mac" then
  echo("\nYou're on a mac and VoiceOver will skip reading text when there's lots of it coming in. Would you like to install a third-party TTS plugin to alleviate this issue?\n")
  echo("Type 'mudlet access reader' if so.\n")
  echo("See https://wiki.mudlet.org/w/Accessibility_on_OSX if you'd like to learn more.\n")
end

echo("\nThat's all, enjoy Mudlet!\n")

local number = page_number()
echo("page #:"..number)
expandAlias("song "..number)

if mrf_or_pozhen == "mrf" then
  echo("mrf: --> "..murong_task["murong_target_city"]..":"..murong_task["murong_target_room"])
elseif mrf_or_pozhen == "pozhen" then
  echo("pozhen: --> "..pozhen_task.city..":"..pozhen_task.room)
else
  echo("?? which task?? \n")
end

Script.script:
current_position = ""
wuxing = { mu = {p_pos = "huo", n_pos = "tu", name="木"}, huo = {p_pos = "tu", n_pos = "jin", name="火"}, tu = {p_pos = "jin", n_pos = "shui", name="土"}, jin = {p_pos = "shui", n_pos = "mu", name="金"}, shui = {p_pos = "mu", n_pos = "huo", name="水"}, }
local function wuxing_ch_2_en(name) if name == "木" then return "mu" elseif name == "火" then return "huo" elseif name == "土" then return "tu" elseif name == "金" then return "jin" else return "shui" end end -- direction = {正, 反} -- position = {"木", "火", "土", "金", "水"} -- return = new position as {"木", "火", "土", "金", "水"} function initialize_wuxing(direction, position) wuxing_direction = direction current_position = wuxing_ch_2_en(position) return current_position end
-- direction = {正, 反} -- return = new position as {"木", "火", "土", "金", "水"}
function wuxing_zouwei(direction) local dir = "p_pos" if direction == "反" then dir = "n_pos" end current_position = wuxing[current_position][dir] return current_position end
jing_content = "" function reset_jing_content() jing_content = "" end function build_jing_content(sentence) jing_content = jing_content..sentence --echo("build_jing_content => :"..jing_content.."\n") end function get_jing_content() return jing_content end
songjing_info = { address = "", book_name = "", -- 庄子·外篇「上卷」 book_chapter = "", -- 庄子·外篇·天地 page_number_in_chapter = 0, chapter_start_page_number = 0, chapter = "" -- 天地 }
function page_number() return songjing_info.chapter_start_page_number + songjing_info.page_number_in_chapter - 1 end
local function parse_jing(addr, book, whole_chapter, page) songjing_info.address = addr songjing_info.book_name = book songjing_info.book_chapter = whole_chapter songjing_info.page_number_in_chapter = page songjing_info.chapter = get_chapter(whole_chapter) songjing_info.chapter_start_page_number = find_zhuangzi_chapter_start_page(songjing_info.chapter) end
local zhuangzi = { --庄子·外篇「下卷」(Zhuang zi) {start = 1, name = "秋水"}, {start = 51, name = "至乐"}, {start = 69, name = "达生"}, {start = 107, name = "山木"}, {start = 143, name = "田子方"}, {start = 177, name = "知北游"}, --庄子·杂篇「上卷」 {start = 1, name = "庚桑楚"}, {start = 42, name = "徐无鬼"}, {start = 98, name = "则阳"}, {start = 141, name = "外物"}, {start = 168, name = "寓言"}, --庄子·外篇「上卷」 {start = 1, name = "骈拇"}, {start = 4, name = "马蹄"}, {start = 12, name = "胠箧"}, {start = 31, name = "在宥"}, {start = 66, name = "天地"}, {start = 116, name = "天道"}, {start = 151, name = "天运"}, {start = 190, name = "刻意"}, {start = 201, name = "缮性"}, --庄子·内篇「下卷」(Zhuang zi) {start = 1, name = "人间世"}, {start = 46, name = "德充符"}, {start = 77, name = "大宗师"}, {start = 124, name = "应帝王"}, --庄子·杂篇「下卷」(Zhuang zi) {start = 1, name = "让王"}, {start = 47, name = "盗跖"}, {start = 99, name = "说剑"}, {start = 114, name = "渔父"}, {start = 139, name = "列御寇"}, {start = 169, name = "天下"}, --庄子·内篇「上卷」(Zhuang zi) {start = 169, name = "逍遥游"}, {start = 169, name = "齐物论"}, {start = 169, name = "养身主"} }
local function get_chapter(whole_chapter) local str = split_string(whole_chapter, "·") local chapter = str[#str] echo("===> chapter: "..chapter) return chapter end
local function find_zhuangzi_chapter_start_page(chapter) for k, v in pairs(zhuangzi) do if chapter == v["name"] then echo("-->page: "..v["start"].."\n") return v["start"] end end echo("===> 没有发现章节: "..chapter.."\n") return nil end
