wuxing = { mu = {p_pos = "huo", n_pos = "tu", name="木"}, huo = {p_pos = "tu", n_pos = "jin", name="火"}, tu = {p_pos = "jin", n_pos = "shui", name="土"}, jin = {p_pos = "shui", n_pos = "mu", name="金"}, shui = {p_pos = "mu", n_pos = "huo", name="水"}, }
local function wuxing_ch_2_en(name) if name == "木" then return "mu" elseif name == "火" then return "huo" elseif name == "土" then return "tu" elseif name == "金" then return "jin" else return "shui" end end -- direction = {正, 反} -- position = {"木", "火", "土", "金", "水"} -- return = new position as {"木", "火", "土", "金", "水"} function initialize_wuxing(direction, position) wuxing_direction = direction current_position = wuxing_ch_2_en(position) return current_position end
-- direction = {正, 反} -- return = new position as {"木", "火", "土", "金", "水"}
