function mzj_path_back(room)
  if mzj_room[room] ~= nil then
    return mzj_room[room].back_to_meng
  else
    echo("=== 不存在这个mzj房间\n")
    return nil
  end   
end

function mzj_to_waiting_room(room)
  local path = mzj_room[room].path
  if path == nil then      
    echo(" == 需要补充这个地点 - "..room)
  else
    send(path)  
  end  
end

